float32_t FFT_In[FFT_Len*2];
float32_t	FFT_IN_Non[FFT_Len*2];
float32_t FFT_Mag[FFT_Len];
void FFT_TEST(void)
{
	uint16_t i;
// 去除直流分量后放入 FFT_In 的高位
// 可以考虑等比例扩大FFT_In[2*i]
	//ADC_SourceData是int16_t
	u32 temp;
	temp=0;
	for(i=0;i<FFT_Len;i++){
		temp+=ADC_SourceData[i];
	}
	float mean;
	mean=(float)temp/FFT_Len;
	if(!improve){
				for(i=0;i<FFT_Len;i++)
				{
					FFT_In[2*i]=((float)ADC_SourceData[i]-mean);   //((ADC_SourceData[i])-2048)去直流(0.5*arm_sin_f32(2*Pi*1000/Fs*i)+0.25*arm_sin_f32(2*Pi*2000/Fs*i)+0.1*arm_sin_f32(2*Pi*3000/Fs*i));
					//程序生成数据不加窗状态下结果极佳，加窗后比例不对
					FFT_In[2*i+1]=0;
				}
	}
	else{
		if(test_num<=3){
			float temp;
				for(i=0;i<FFT_Len;i++)
				{
					temp=((float)ADC_SourceData[i]-mean);
					FFT_In[2*i]=temp;   //*hanning_win_table[i]//((ADC_SourceData[i])-2048)去直流(0.5*arm_sin_f32(2*Pi*1000/Fs*i)+0.25*arm_sin_f32(2*Pi*2000/Fs*i)+0.1*arm_sin_f32(2*Pi*3000/Fs*i));
					//程序生成数据不加窗状态下结果极佳，加窗后比例不对
//					FFT_IN_Non[2*i]=temp;
					FFT_In[2*i+1]=0;
//					FFT_IN_Non[2*i+1]=0;
				}
		}
		else{
				for(i=0;i<FFT_Len;i++)
				{
					temp=((float)ADC_SourceData[i]-mean);
					FFT_In[2*i]=temp*blackman_win_table[i];   //((ADC_SourceData[i])-2048)去直流(0.5*arm_sin_f32(2*Pi*1000/Fs*i)+0.25*arm_sin_f32(2*Pi*2000/Fs*i)+0.1*arm_sin_f32(2*Pi*3000/Fs*i));
					//程序生成数据不加窗状态下结果极佳，加窗后比例不对
//					FFT_IN_Non[2*i]=temp;
					FFT_In[2*i+1]=0;
//					FFT_IN_Non[2*i+1]=0;
				}
				}
		}
		arm_cfft_f32(&arm_cfft_sR_f32_len256,FFT_In ,0,1);//注意的是输入和输出共用一块缓存
		arm_cmplx_mag_f32(FFT_In, FFT_Mag, FFT_Len);
		//Get mag
		FFT_Mag[0]=FFT_Mag[0]/2;
		//获取相位在main函数中完成
}
